import React, {useEffect, useState} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import Basket from "./Basket";
import {CountBasketProducts, countBasketThunk, deleteInBasketThunk, getBasketThunk} from "../../Redux/Reduser";


const BasketContainer = (props) => {



    const deleteSmart = (id) => {
        const delSmart = props.basket
        props.deleteInBasketThunk(id, delSmart);
    }

    const CountBasket = (id, count) => {
        if (count === 0) {
            deleteSmart(id)

        } else {
            props.countBasketThunk(id, count);

        }

    }
    useEffect(() => {


            props.getBasketThunk();



    }, []);



    return <Basket CountBasket={CountBasket} deleteSmart={deleteSmart} {...props}/>
}


const mapStateToProps = (state) => {
    return {
        basket: state.Reduser.basket,
        countBasketProducts: state.Reduser.countBasketProducts
    }
}

export default compose(
    connect(mapStateToProps, {getBasketThunk, deleteInBasketThunk, countBasketThunk, CountBasketProducts}),
    withRouter
)(BasketContainer)
