import React, {useEffect, useState} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {
    getAppleThunk,
    getOneAppleThunk,
    getXiaomiThunk,
    getOneXiaomiThunk,
    getDescriptionThunk, addInBasketThunk, countBasketThunk, getBasketThunk
} from "../../Redux/Reduser";
import WindowProduct from "./WindowProduct";


const WindowContainer = ({getOneXiaomiThunk,getOneAppleThunk,getDescriptionThunk,addInBasketThunk,getBasketThunk,countBasketThunk,...props}) => {



    useEffect(()=>{
        let id=props.match.params.id;
        if(id>=10) {
            getOneXiaomiThunk(id);
        }
    },[getOneXiaomiThunk])


    useEffect(()=>{
        let id = props.match.params.id;
        if(id<10) {
            getOneAppleThunk(id);
        }
    },[getOneAppleThunk])

useEffect(()=>{
            getDescriptionThunk();
    },[getDescriptionThunk])


    const addSmart=(name,photo,price)=>{
            addInBasketThunk(name,photo,price);

    }

    const CountBasket=(id,count)=>{
        countBasketThunk(id,count);
    }
    useEffect(()=>{
        getBasketThunk();

    },[getBasketThunk])





    return<WindowProduct CountBasket={CountBasket} addSmart={addSmart} {...props}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        oneApple: state.Reduser.oneApple,
        oneXiaomi:state.Reduser.oneXiaomi,
        description: state.Reduser.description,
        basket:state.Reduser.basket
    }
}

export default compose(
    connect(mapStateToProps,{getXiaomiThunk,getAppleThunk,getOneAppleThunk,
        getOneXiaomiThunk,getDescriptionThunk,addInBasketThunk,countBasketThunk,getBasketThunk}),
    withRouter
)(WindowContainer)
