import React, {useEffect} from "react";

import {connect} from "react-redux";
import {getAppleThunk, getXiaomiThunk} from "./Redux/Reduser";
import App from "./App";


const AppContainer = (props) => {
    useEffect(()=>{
        props.getAppleThunk();
    },[])
    useEffect(()=>{
        props.getXiaomiThunk();
    },[])

    return<App {...props}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi
    }
}

export default connect(mapStateToProps,{getAppleThunk,getXiaomiThunk})(AppContainer)
