import React, {useEffect} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {
    getAppleThunk,
    getOneAppleThunk,
    getXiaomiThunk,
    getOneXiaomiThunk,
    getDescriptionThunk
} from "../../Redux/Reduser";
import WindowProduct from "./WindowProduct";


const WindowContainer = (props) => {

    useEffect(()=>{
        let id=props.match.params.id;
        if(id>=10) {
            props.getOneXiaomiThunk(id);
        }
    },[])


    useEffect(()=>{
        let id = props.match.params.id;
        if(id<10) {
            props.getOneAppleThunk(id);
        }
    },[])

useEffect(()=>{
            props.getDescriptionThunk();
    },[])

    return<WindowProduct {...props}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        oneApple: state.Reduser.oneApple,
        oneXiaomi:state.Reduser.oneXiaomi,
        description: state.Reduser.description
    }
}

export default compose(
    connect(mapStateToProps,{getXiaomiThunk,getAppleThunk,getOneAppleThunk,getOneXiaomiThunk,getDescriptionThunk}),
    withRouter
)(WindowContainer)
