import React, {useEffect} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {getAppleThunk, getXiaomiThunk, searchAC} from "../../Redux/Reduser";
import Header from "./Header";


const HeaderContainer = ({getAppleThunk,getXiaomiThunk,apple,xiaomi,countBasketProducts,Search,searchAC}) => {

    useEffect(()=>{
        getAppleThunk();
    },[getAppleThunk])
    useEffect(()=>{
        getXiaomiThunk();
    },[getXiaomiThunk])


    return<Header searchs={searchAC}
                  apple={apple}
                  xiaomi={xiaomi}
                  countBasketProducts={countBasketProducts}
                  Search={Search} />
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        Search:state.Reduser.search,
        countBasketProducts:state.Reduser.countBasketProducts
    }
}

export default compose(
    connect(mapStateToProps,{getXiaomiThunk,getAppleThunk,searchAC}),
    withRouter
)(HeaderContainer)
