import React, {useEffect} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {getAppleThunk, getOneAppleThunk, getXiaomiThunk} from "../../Redux/Reduser";
import WindowProduct from "./WindowProduct";


const WindowContainer = (props) => {

    useEffect(()=>{
        let id=props.match.params.id;
        props.getOneXiaomiThunk(id);
    },[])


    useEffect(()=>{
        let id=props.match.params.id;
        props.getOneAppleThunk(id);
    },[])

    return<WindowProduct {...props}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        oneApple: state.Reduser.oneApple,
        oneXiaomi:state.Reduser.oneXiaomi
    }
}

export default compose(
    connect(mapStateToProps,{getXiaomiThunk,getAppleThunk,getOneAppleThunk,getOneXiaomi}),
    withRouter
)(WindowContainer)
