import React from "react";
import styles from"./Window.module.css"


const WindowProduct =(props)=>{

    console.log(props.description)

    let Description=props.description.filter(des=>props.oneApple.id===des.listId ||props.oneXiaomi.id===des.listId)
        .map(d=><Descriptions key={d.id}{...d}/>)


    return<div className={styles.window}>
    <div className={styles.image}>
        <img src={props.oneXiaomi.photo }/>
        <img src={props.oneApple.photo}/>
    </div>
        <div className={styles.name}>{props.oneApple.name}{props.oneXiaomi.name}</div>
        <div className={styles.Descriptions}>
            {Description}
        </div>
    </div>
}


export const Descriptions = (props) => {
    return <div>{props.display}</div>
}
export const DescriptionTitle = ({Title, Value}) => {
    return <div><b>{Title}</b> : {Value}</div>
}



export default WindowProduct;
