import React from "react";
import {Field, reduxForm,reset} from "redux-form";

const Feedback=({feedback,addFeedback,oneApple,oneXiaomi})=>{
    const AddFeedBack=(value)=>{
        if(oneApple.length===0){
let id=oneXiaomi.id;
        addFeedback(value.FeedBackName,id,value.FeedBack,1)}
        else{
            let id= oneApple.id
            addFeedback(value.FeedBackName,id,value.FeedBack,1)
        }
    }

    let FeedBackElements= feedback.filter(f=>f.productId===oneXiaomi.id || f.productId===oneApple.id).map(fbe=><div key={fbe.id}>
        <span>{fbe.name}</span><span>{fbe.feedBack}</span>
    </div>)
    return<div>feedback
        <div>
            <FeedBackReduxForm onSubmit={AddFeedBack}/>
        </div>
    <div>
        {FeedBackElements}
    </div>
    </div>
}

const FeedBackForm=({handleSubmit})=>{
    return<form  onSubmit={handleSubmit}>
        <Field component={"input"} name={"FeedBackName"} />
        <Field component={"textarea"} name={"FeedBack"} />
        <button >add</button>
    </form>
}
const afterSubmit = (result, dispatch) =>
    dispatch(reset('feedback'));
const FeedBackReduxForm=reduxForm({form:"feedback",onSubmitSuccess: afterSubmit})(FeedBackForm)





export default Feedback
