import React, {useEffect, useState} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import Basket from "./Basket";
import {CountBasketProducts, countBasketThunk, deleteInBasketThunk, getBasketThunk} from "../../Redux/Reduser";


const BasketContainer = (props) => {

    const[Render,setRender]=useState(false)
    const[eRender,seteRender]=useState(false)
    const[sRender,setsRender]=useState(false)

    const deleteSmart = (id) => {
        const delSmart = props.basket
        props.deleteInBasketThunk(id, delSmart);
    }

    const CountBasket=(id,count)=>{
        if(count===0){
            deleteSmart(id)
            setRender(true)
        }
        else {
            props.countBasketThunk(id, count);
            seteRender(true)
        }
    }
    const CountBasketProduct=(countBasketProducts)=>{
        props.CountBasketProducts(countBasketProducts)
        setsRender(true)
    }

    useEffect(() => {

            props.getBasketThunk();
            setsRender(false)
        setRender(false)
        seteRender(false)

    }, [])



    return <Basket CountBasketProduct={CountBasketProduct} CountBasket={CountBasket}  deleteSmart={deleteSmart} {...props}/>
}


const mapStateToProps = (state) => {
    return {
        basket: state.Reduser.basket,
        countBasketProducts:state.Reduser.countBasketProducts
    }
}

export default compose(
    connect(mapStateToProps, {getBasketThunk, deleteInBasketThunk,countBasketThunk,CountBasketProducts}),
    withRouter
)(BasketContainer)
