import React, {useEffect, useState} from "react";
import FeedBack from "./FeedBack";
import {connect} from "react-redux";
import {addFeedBackThunk, getFeedBackThunk,initializeFeedbackThunk} from "../../Redux/Reduser";

export const FeedBackContainers=({getFeedBackThunk,feedback,addFeedBackThunk,oneApple,oneXiaomi,initializeFeedbackThunk})=>{

    const[render,setRender]=useState(true)
    const addFeedback=(name,id,feedBack,rating)=>{
        initializeFeedbackThunk(name,id,feedBack,rating)
        setRender(true)
    }
    useEffect(()=>{
        if(render){
        getFeedBackThunk();
        setRender(false)
        }
    },[render,getFeedBackThunk,addFeedBackThunk])

    useEffect(()=>{
        initializeFeedbackThunk();
    },[initializeFeedbackThunk])


    return <FeedBack addFeedback={addFeedback} oneApple={oneApple} oneXiaomi={oneXiaomi} feedback={feedback}/>
}

const mapStateToProps=(state)=>{
    return{
        feedback:state.Reduser.feedback,
        oneApple:state.Reduser.oneApple,
        oneXiaomi:state.Reduser.oneXiaomi,
        initializeFeedback: state.Reduser.initializeFeedback
    }
}

export default connect(mapStateToProps,{getFeedBackThunk,addFeedBackThunk,initializeFeedbackThunk})(FeedBackContainers)
