import React, {useEffect} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {
    getAppleThunk,
    getOneAppleThunk,
    getXiaomiThunk,
    getOneXiaomiThunk,
    getDescriptionThunk, addInBasketThunk, countBasketThunk, getBasketThunk
} from "../../Redux/Reduser";
import Product from "./Product";


const ProductContainer = (props) => {

    useEffect(()=>{
        let id=props.match.params.id;
        if(id>=10) {
            props.getOneXiaomiThunk(id);
        }
    },[])


    useEffect(()=>{
        let id = props.match.params.id;
        if(id<10) {
            props.getOneAppleThunk(id);
        }
    },[])



    const addSmart=(name,photo,price)=>{
            props.addInBasketThunk(name,photo,price);
    }

    const CountBasket=(id,count)=>{
        props.countBasketThunk(id,count);
    }
    useEffect(()=>{
        props.getBasketThunk();
    },[CountBasket])


    return<Product CountBasket={CountBasket} addSmart={addSmart} {...props}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        oneApple: state.Reduser.oneApple,
        oneXiaomi:state.Reduser.oneXiaomi,
        description: state.Reduser.description,
        basket:state.Reduser.basket
    }
}

export default compose(
    connect(mapStateToProps,{getXiaomiThunk,getAppleThunk,getOneAppleThunk,
        getOneXiaomiThunk,getDescriptionThunk,addInBasketThunk,
        countBasketThunk,getBasketThunk}),
    withRouter
)(ProductContainer)
