import React, {useState} from "react";
import {Field, reduxForm, reset} from "redux-form";
import styles from "./FeedBack.module.css"
import MyPreloader from "../Preloader/Preloader";

const Feedback = ({feedback, addFeedback, oneApple, oneXiaomi,initializeFeedback}) => {
    const AddFeedBack = (value) => {
        if (oneApple.length === 0) {
            let id = oneXiaomi.id;
            addFeedback(value.FeedBackName, id, value.FeedBack, 1)
        } else {
            let id = oneApple.id
            addFeedback(value.FeedBackName, id, value.FeedBack, 1)
        }
    }

    let FeedBackElements = feedback.filter(f => f.productId === oneXiaomi.id || f.productId === oneApple.id).map((fbe) =>{
       let realRating="";

        for(let i=0;i<fbe.rating;i++){
            realRating+="+"
        }
        let allRating=""
        let c=5-fbe.rating
        for(let j=0; j<c;j++){
            allRating+="-"
        }


        return<div key={fbe.id}>
            {fbe.name !== undefined ?
                <div>
                    <div>
                        <span>Name: </span>
                        <span className={styles.feedbackName}>{fbe.name}</span>
                        <span>{realRating}{allRating}</span>
                    </div>
                    <div>
                        <span>Feedback: </span>
                        <span className={styles.feedbackText}>{fbe.feedBack}</span>
                    </div>
                </div> : null}
        </div>})




        return <div className={styles.feedBack}>feedback
            <div>
                <FeedBackReduxForm onSubmit={AddFeedBack}/>
            </div>
            {initializeFeedback &&
            <div>
                {FeedBackElements}
            </div>
            }
            {!initializeFeedback &&
            <MyPreloader/>
            }
        </div>
    }


const FeedBackForm = ({handleSubmit}) => {
    const[check,setCheck]=useState(0)
    const check1 = () => {
        setCheck(1)

    }
    const check2 = () => {
        setCheck(2)

    }
    const check3 = () => {
        setCheck(3)

    }
    const check4 = () => {
        setCheck(4)

    }
    const check5 = () => {
        setCheck(5)

    }




    return <form onSubmit={handleSubmit}>
        <Field className={styles.fieldName} placeholder={" your name"} component={"input"} name={"FeedBackName"}/>
        <Field className={styles.fieldCheckBox} onClick={check1} component={"input"} type={"checkbox"}  name={"checkBox1"}/>
        <Field className={styles.fieldCheckBox} onChange={check2} component={"input"} type={"checkbox"} name={"checkBox2"}/>
        <Field className={styles.fieldCheckBox} onChange={check3} component={"input"} type={"checkbox"} name={"checkBox3"}/>
        <Field className={styles.fieldCheckBox} onChange={check4} component={"input"} type={"checkbox"} name={"checkBox4"}/>
        <Field className={styles.fieldCheckBox} onChange={check5} component={"input"} type={"checkbox"} name={"checkBox5"}/>
        <Field className={styles.fieldFeedback} placeholder={" your feedback"} component={"textarea"} name={"FeedBack"}/>


        <button>add</button>
    </form>
}
const afterSubmit = (result, dispatch) =>
    dispatch(reset('feedback'));
const FeedBackReduxForm = reduxForm({form: "feedback", onSubmitSuccess: afterSubmit})(FeedBackForm)


export default Feedback
