import React, {useEffect} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import { getXiaomiThunk} from "../../Redux/Reduser";
import Xiaomi from "./Xiaomi";


const XiaomiContainer = (props) => {

    useEffect(()=>{
        props.getXiaomiThunk();
    },[])
    return<Xiaomi {...props}/>
}



const mapStateToProps = (state) => {
    return {
        xiaomi: state.Reduser.xiaomi
    }
}

export default compose(
    connect(mapStateToProps,{getXiaomiThunk}),
    withRouter
)(XiaomiContainer)
