import React, {useEffect} from "react";

import {connect} from "react-redux";
import {
    CountBasketProducts,
    getAppleThunk, getBasketThunk,
    getXiaomiThunk, initializeApp
} from "./Redux/Reduser";
import App from "./App";


const AppContainer = ({getAppleThunk,getXiaomiThunk,getBasketThunk,CountBasketProducts,basket,initializeApp,initializeFeedback,initializeFeedbackAC}) => {



    let Count = basket.map(c => c.count);


    useEffect(  () => {
        let a=0
        for (let i = 0; i < basket.length; i++) {
            a=a+Count[i]
        }
        CountBasketProducts(a);

    })

    useEffect(()=>{
        getAppleThunk();
    },[getAppleThunk])
    useEffect(()=>{
        getXiaomiThunk();
    },[ getXiaomiThunk])
    useEffect(()=>{
        getBasketThunk();
    },[getBasketThunk])


    useEffect(()=>{
        initializeFeedbackAC(false)
        initializeApp();
    },[initializeApp])

 return <App initializeFeedback={initializeFeedback}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        basket:state.Reduser.basket,
        initializeFeedback:state.Reduser.initializeFeedback


    }
}

export default connect(mapStateToProps,{getAppleThunk,getXiaomiThunk,getBasketThunk,CountBasketProducts,initializeApp})(AppContainer)
