import React, {useEffect, useState} from "react";

import {connect} from "react-redux";
import {
    countBasket,
    CountBasketProducts,
    getAppleThunk, getBasketThunk,
    getXiaomiThunk
} from "./Redux/Reduser";
import App from "./App";


const AppContainer = ({getAppleThunk,getXiaomiThunk,basket,CountBasketProducts,getBasketThunk}) => {

   
    useEffect(()=>{
        getAppleThunk();

    },[getAppleThunk])
    useEffect(()=>{
        getXiaomiThunk();

    },[ getXiaomiThunk])


        let Price = basket.map(p => p.price);
        let Count = basket.map(c => c.count);
        let total = 0;
        for (let i = 0; i < basket.length; i++) {
            total = total + Price[i] * Count[i]
        }


    let countBasket = 0;
    for (let i = 0; i < basket.length; i++) {
        countBasket = countBasket + Count[i]
    }
    CountBasketProducts(countBasket);




    useEffect(() => {
            getBasketThunk();

    }, [AppContainer,getBasketThunk]);

    return<App />
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        basket:state.Reduser.basket,
        countBasketProducts:state.Reduser.countBasketProducts

    }
}

export default connect(mapStateToProps,{getAppleThunk,getXiaomiThunk,CountBasketProducts,getBasketThunk})(AppContainer)
