import React, {useState} from "react";
import styles from"./Basket.module.css"

const Basket =(props)=> {
    let BasketElements=props.basket.map(b=><BasketElement key={b.id} {...b}/>)
    return<div className={styles.basket}>
        <span>Basket</span>
<div>
    {BasketElements}
</div>
    </div>
}

export default Basket;

const BasketElement = (props)=> {
    const[count,setCount]=useState(1)
    let CountMinus = ()=>{
        setCount(count-1)
    }
    let CountPlus = ()=>{
        setCount(count+1)
    }
    return<div className={styles.basketElement}>
        <img src={props.photo} alt="photoSmartphone"/>
        {props.name}
        <button onClick={CountMinus}>-</button>
        <input className={styles.count} readOnly value={count} type="number" min="0"max="10"/>
        <button onClick={CountPlus}>+</button>
        {props.price}
    </div>

}
