import React, {useState} from "react";
import styles from "./SearchBox.module.css"
import {Link} from "react-router-dom";


const SearchBox = ({searchBox,apple,xiaomi,searchBoxAC,searchAC}) => {

    const[searchName,setSearchName]=useState("")
const search = (e)=>{
        setSearchName(e.currentTarget.value)
    searchBoxAC(searchName)
}
    let AppleElementSearch = apple.filter(f => f.name.includes(searchBox)).map(ae =>
        <div value={ae.name}><Link to={'/smart/'+ae.id}key={ae.id} className={styles.searchBoxElements}>
            {ae.name}
        </Link></div>)

    let XiaomiElementSearch = xiaomi.filter(f => f.name.includes(searchBox)).map(ae =>
       <div> <Link onClick={search} to={'/smart/'+ae.id} key={ae.id} className={styles.searchBoxElements}>
            {ae.name}
       </Link></div>)


    return <div className={styles.searchBox}>
        {AppleElementSearch}
            {XiaomiElementSearch}
    </div>

}

export default SearchBox;
