import React, {useState} from "react";
import {Field, reduxForm, reset} from "redux-form";
import styles from "./FeedBack.module.css"
import MyPreloader from "../Preloader/Preloader";
import star from "../../Assets/img/star.svg"
import starWhite from "../../Assets/img/starWhite.svg"

const Feedback = ({feedback, addFeedback, oneApple, oneXiaomi, initializeFeedback}) => {
    const AddFeedBack = (value) => {
        let date = new Date().toLocaleString([year, month, day, hour, minute])
        if (oneApple.length === 0) {
            let id = oneXiaomi.id;
            addFeedback(value.FeedBackName, id, value.FeedBack, check, date)
        } else {
            let id = oneApple.id
            addFeedback(value.FeedBackName, id, value.FeedBack, check, date)
        }
        setCheck(1)
    }


    let FeedBackElements = feedback.filter(f => f.productId === oneXiaomi.id || f.productId === oneApple.id).reverse().map((fbe) => {
        let realRating = [];

        for (let i = 0; i < fbe.rating; i++) {
            realRating[i] += {a: "+", id: i}

        }
        let allRating = []
        let c = 5 - fbe.rating
        for (let j = 0; j < c; j++) {
            allRating[j] += {a: "-", id: j}
        }

        let realRatingImg = realRating.map(d => <img key={d.id} alt={"star"} height={15} src={star}/>)
        let allRatingImg = allRating.map(x => <img key={x.id} alt={"starWhite"} height={15} src={starWhite}/>)

        return <div key={fbe.id}>
            {fbe.name !== undefined ?
                <div>
                    <div>
                        <span>Name: </span>
                        <span className={styles.feedbackName}>{fbe.name}</span>
                        <span>{fbe.date}</span>
                        <span>{realRatingImg}{allRatingImg}</span>
                    </div>
                    <div>
                        <span>Feedback: </span>
                        <span className={styles.feedbackText}>{fbe.feedBack}</span>
                    </div>
                </div> : null}
        </div>
    })

    const [check, setCheck] = useState(1)
    const check1 = () => {
        setCheck(1)
    }
    const check2 = () => {
        setCheck(2)
    }
    const check3 = () => {
        setCheck(3)
    }
    const check4 = () => {
        setCheck(4)
    }
    const check5 = () => {
        setCheck(5)
    }

    return <div className={styles.feedBack}>feedback
        <div>
            <FeedBackReduxForm check={check} check1={check1} check2={check2} check3={check3} check4={check4}
                               check5={check5} onSubmit={AddFeedBack}/>
        </div>
        {initializeFeedback &&
        <div>
            {FeedBackElements}
        </div>
        }
        {!initializeFeedback &&
        <MyPreloader/>
        }
    </div>
}


const FeedBackForm = ({handleSubmit, check, check1, check2, check3, check4, check5, time}) => {


    return <form onSubmit={handleSubmit}>
        <Field className={styles.fieldName} placeholder={" your name"} component={"input"} name={"FeedBackName"}/>
        {(check === 1 || check === 2 || check === 3 || check === 4 || check === 5) &&
        <img className={styles.check} onClick={check1} alt={"star1"} height={15} src={star} name={"star1"}/>
        }
        {check !== 1 && check !== 2 && check !== 3 && check !== 4 && check !== 5 &&
        <img className={styles.check} onClick={check1} alt={"starWhite1"} height={15} src={starWhite} name={"star1"}/>
        }
        {(check === 2 || check === 3 || check === 4 || check === 5) &&
        <img className={styles.check} onClick={check2} alt={"star2"} height={15} src={star} name={"star1"}/>
        }
        {check !== 2 && check !== 3 && check !== 4 && check !== 5 &&
        <img className={styles.check} onClick={check2} alt={"starWhite2"} height={15} src={starWhite} name={"star2"}/>
        }
        {(check === 3 || check === 4 || check === 5) &&
        <img className={styles.check} onClick={check3} alt={"star3"} height={15} src={star} name={"star3"}/>
        }
        {check !== 3 && check !== 4 && check !== 5 &&
        <img className={styles.check} onClick={check3} alt={"starWhite3"} height={15} src={starWhite} name={"star3"}/>
        }
        {(check === 4 || check === 5) &&
        <img className={styles.check} onClick={check4} alt={"star4"} height={15} src={star} name={"star4"}/>
        }
        {check !== 4 && check !== 5 &&
        <img className={styles.check} onClick={check4} alt={"starWhite4"} height={15} src={starWhite} name={"star4"}/>
        }
        {check === 5 &&
        <img className={styles.check} onClick={check5} alt={"star5"} height={15} src={star} name={"star5"}/>
        }
        {check !== 5 &&
        <img className={styles.check} onClick={check5} alt={"starWhite5"} height={15} src={starWhite} name={"star5"}/>
        }
        <Field className={styles.fieldFeedback} placeholder={" your feedback"} component={"textarea"}
               name={"FeedBack"}/>


        <button>add</button>
    </form>
}
const afterSubmit = (result, dispatch) =>
    dispatch(reset('feedback'));
const FeedBackReduxForm = reduxForm({form: "feedback", onSubmitSuccess: afterSubmit})(FeedBackForm)


export default Feedback
