import React from "react";
import styles from "./Main.module.css"
import ProductContainer from "../Product/ProductContainer";

const Main = ({Search,apple,xiaomi,searchAC}) => {
    const onBlur= ()=>{
        searchAC("searchName")
    }

    if (Search !== "") {
        let AppleElementSearch = apple.filter(f => f.name === Search).map(ae => <ProductContainer onBlur={onBlur} key={ae.id} {...ae}/>)


        let XiaomiElementSearch = xiaomi.filter(f => f.name === Search).map(ae => <ProductContainer
            onBlur={onBlur} key={ae.id} {...ae}/>)
        return <div className={styles.main}>
            {AppleElementSearch}
            {XiaomiElementSearch}
        </div>
    } else if (Search === "") {

        let AppleElements = apple.map(ae => <ProductContainer onBlur={onBlur} key={ae.id} {...ae}/>)
        let XiaomiElements = xiaomi.map(ae => <ProductContainer onBlur={onBlur} key={ae.id} {...ae}/>)


        return <div className={styles.main}>
            <div>Apple</div>
            <div>
                {AppleElements}
            </div>

            <div>
                Xiaomi
            </div>
            <div>
                {XiaomiElements}
            </div>


        </div>
    }
}

export default Main;
