import React, {useEffect} from "react";
import Main from "./Main";
import {connect} from "react-redux";
import {getAppleThunk, getXiaomiThunk} from "../../Redux/Reduser";


const MainContainer = (props) => {
    useEffect(()=>{
        props.getAppleThunk();
    },[])
    useEffect(()=>{
        props.getXiaomiThunk();
    },[])

    return<Main {...props}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi
    }
}

export default connect(mapStateToProps,{getAppleThunk,getXiaomiThunk})(MainContainer)
