import React from "react";
import {Field, reduxForm, reset} from "redux-form";
import styles from "./FeedBack.module.css"
import MyPreloader from "../Preloader/Preloader";

const Feedback = ({feedback, addFeedback, oneApple, oneXiaomi,initializeFeedback}) => {
    const AddFeedBack = (value) => {
        if (oneApple.length === 0) {
            let id = oneXiaomi.id;
            addFeedback(value.FeedBackName, id, value.FeedBack, 1)
        } else {
            let id = oneApple.id
            addFeedback(value.FeedBackName, id, value.FeedBack, 1)
        }
    }

    let FeedBackElements = feedback.filter(f => f.productId === oneXiaomi.id || f.productId === oneApple.id).map(fbe =>
        <div key={fbe.id}>
            {fbe.name !== undefined ?
                <div>
                    <div>
                        <span>Name: </span>
                        <span className={styles.feedbackName}>{fbe.name}</span></div>
                    <div>
                        <span>Feedback: </span>
                        <span className={styles.feedbackText}>{fbe.feedBack}</span>
                    </div>
                </div> : null}
        </div>)


        return <div className={styles.feedBack}>feedback
            <div>
                <FeedBackReduxForm onSubmit={AddFeedBack}/>
            </div>
            {initializeFeedback &&
            <div>
                {FeedBackElements}
            </div>
            }
            {!initializeFeedback &&
            <MyPreloader/>
            }
        </div>
    }


const FeedBackForm = ({handleSubmit}) => {
    return <form onSubmit={handleSubmit}>
        <Field className={styles.fieldName} placeholder={" your name"} component={"input"} name={"FeedBackName"}/>
        <Field className={styles.fieldCheckBox} component={"input"} type={"radio"}  name={"checkBox1"}/>
        <Field className={styles.fieldCheckBox} component={"input"} type={"radio"} name={"checkBox2"}/>
        <Field className={styles.fieldCheckBox} component={"input"} type={"radio"} name={"checkBox3"}/>
        <Field className={styles.fieldCheckBox} component={"input"} type={"radio"} name={"checkBox4"}/>
        <Field className={styles.fieldCheckBox} component={"input"} type={"radio"} name={"checkBox5"}/>
        <Field className={styles.fieldFeedback} placeholder={" your feedback"} component={"textarea"} name={"FeedBack"}/>


        <button>add</button>
    </form>
}
const afterSubmit = (result, dispatch) =>
    dispatch(reset('feedback'));
const FeedBackReduxForm = reduxForm({form: "feedback", onSubmitSuccess: afterSubmit})(FeedBackForm)


export default Feedback
