import React, {useEffect} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {getAppleThunk, initializeApple, initializeFeedbackAC} from "../../Redux/Reduser";
import Apple from "./Apple";


const AppleContainer = ({getAppleThunk,apple,Search,initializeApple,initializeFeedback,initializeFeedbackAC}) => {

    useEffect(()=>{
        initializeFeedbackAC(false);
        initializeApple();
    },[initializeApple])
    return<Apple Search={Search} initializeFeedback={initializeFeedback} apple={apple}  />
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        Search:state.Reduser.search,
        initializeFeedback: state.Reduser.initializeFeedback
    }
}

export default compose(
    connect(mapStateToProps,{getAppleThunk,initializeApple,initializeFeedbackAC}),
    withRouter
)(AppleContainer)
