import React, { useState} from "react";
import styles from "./Header.module.css"
import {Link} from "react-router-dom";
import basket from"../../Assets/img/basket.svg"
import SearchBoxContainer from "../SearchBox/SearchBoxContainer";

const Header = ({Search,searchAC,searchBoxAC,countBasketProducts}) => {

const[searchName,setSearch]=useState(Search);


const inputSearch = (e)=>{
    setSearch(e.currentTarget.value)
    searchBoxAC(e.currentTarget.value)

}

const onSearch = ()=>{
    searchAC(searchName)
    setSearch("")

}
const endSearch= ()=>{
    searchAC("")
}


    return<div className={styles.header}>
        <Link onClick={endSearch} to="/" >Header</Link>
        <input onChange={inputSearch} value={searchName}/>
        { searchName!=="" &&
            <SearchBoxContainer/>
        }
        <button onClick={onSearch}>search</button>
        <Link className={styles.basket} to="/basket">
            <img alt="basket"   src={basket}/>
            {countBasketProducts}
        </Link>

    </div>
}

export default Header;
