import React, {useEffect} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {
    getAppleThunk,
    getOneAppleThunk,
    getXiaomiThunk,
    getOneXiaomiThunk,
    getDescriptionThunk, addInBasketThunk, countBasketThunk, getBasketThunk
} from "../../Redux/Reduser";
import WindowProduct from "./WindowProduct";


const WindowContainer = ({getOneXiaomiThunk,getOneAppleThunk,
                             getDescriptionThunk,addInBasketThunk,
                             getBasketThunk,countBasketThunk,
                             basket,oneApple,oneXiaomi,description,match,CountBasketProducts,...props}) => {



    useEffect(()=>{
        let id=match.params.id;
        if(id>=10) {
            getOneXiaomiThunk(id);
        }
    },[getOneXiaomiThunk,match.params.id])


    useEffect(()=>{
        let id = match.params.id;
        if(id<10) {
            getOneAppleThunk(id);
        }
    },[getOneAppleThunk,match.params.id])

useEffect(()=>{
            getDescriptionThunk();
    },[getDescriptionThunk])


    let Price = basket.map(p => p.price);
    let Count = basket.map(c => c.count);
    let total = 0;
    for (let i = 0; i < basket.length; i++) {
        total = total + Price[i] * Count[i]
    }


    let countBasket = 0;
    for (let i = 0; i < basket.length; i++) {
        countBasket = countBasket + Count[i]
    }
    CountBasketProducts(countBasket);



    const addSmart=(name,photo,price)=>{
            addInBasketThunk(name,photo,price);

    }

    const CountBasket=(id,count)=>{
        countBasketThunk(id,count);
    }
    useEffect(()=>{
        getBasketThunk();

    },[Price,getBasketThunk])







    return<WindowProduct basket={basket}
                         oneApple={oneApple}
                         oneXiaomi={oneXiaomi}
                         description={description}
                         CountBasket={CountBasket}
                         addSmart={addSmart}
                         {...props}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        oneApple: state.Reduser.oneApple,
        oneXiaomi:state.Reduser.oneXiaomi,
        description: state.Reduser.description,
        basket:state.Reduser.basket
    }
}

export default compose(
    connect(mapStateToProps,{getXiaomiThunk,getAppleThunk,getOneAppleThunk,
        getOneXiaomiThunk,getDescriptionThunk,addInBasketThunk,countBasketThunk,getBasketThunk}),
    withRouter
)(WindowContainer)
