import React from "react";
import styles from "./Main.module.css"
import Product from "../Product/Product";
import ProductContainer from "../Product/ProductContainer";

const Main = (props) => {
if(props.Search!==""){
    let AppleElementSearch = props.apple.filter(f=>f.name.toLowerCase().includes(props.Search)).map(ae=><ProductContainer key={ae.id} {...ae}/>)


    let XiaomiElementSearch = props.xiaomi.filter(f=>f.name.includes(props.Search)).map(ae=><ProductContainer key={ae.id} {...ae}/>)
    return<div className={styles.main}>
        {AppleElementSearch}
        {XiaomiElementSearch}
    </div>
}
    let AppleElements = props.apple.map(ae=><ProductContainer key={ae.id} {...ae}/>)
    let XiaomiElements = props.xiaomi.map(ae=><ProductContainer key={ae.id} {...ae}/>)


    return <div className={styles.main}>
        <div>Apple</div>
        <div>
            {AppleElements}
        </div>

        <div>
            Xiaomi
        </div>
        <div>
            {XiaomiElements}
        </div>


    </div>
}

export default Main;
