import React, {useEffect, useState} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {
    getAppleThunk,
    getOneAppleThunk,
    getXiaomiThunk,
    getOneXiaomiThunk,
    getDescriptionThunk, addInBasketThunk, countBasketThunk, getBasketThunk
} from "../../Redux/Reduser";
import WindowProduct from "./WindowProduct";


const WindowContainer = (props) => {

    const[Render,setRender]=useState(false)

    useEffect(()=>{
        let id=props.match.params.id;
        if(id>=10) {
            props.getOneXiaomiThunk(id);
        }
    },[])


    useEffect(()=>{
        let id = props.match.params.id;
        if(id<10) {
            props.getOneAppleThunk(id);
        }
    },[])

useEffect(()=>{
            props.getDescriptionThunk();
    },[])


    const addSmart=(name,photo,price)=>{
            props.addInBasketThunk(name,photo,price);
        setRender(true)
    }

    const CountBasket=(id,count)=>{
        props.countBasketThunk(id,count);
        setRender(true)
    }
    useEffect(()=>{
        props.getBasketThunk();
        setRender(false)
    },[Render])





    return<WindowProduct CountBasket={CountBasket} addSmart={addSmart} {...props}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        oneApple: state.Reduser.oneApple,
        oneXiaomi:state.Reduser.oneXiaomi,
        description: state.Reduser.description,
        basket:state.Reduser.basket
    }
}

export default compose(
    connect(mapStateToProps,{getXiaomiThunk,getAppleThunk,getOneAppleThunk,
        getOneXiaomiThunk,getDescriptionThunk,addInBasketThunk,countBasketThunk,getBasketThunk}),
    withRouter
)(WindowContainer)
