import React, {useEffect, useState} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {
    getAppleThunk,
    getOneAppleThunk,
    getXiaomiThunk,
    getOneXiaomiThunk,
    getDescriptionThunk,
    addInBasketThunk,
    countBasketThunk,
    getBasketThunk,
    searchAC,
    CountBasketProducts
} from "../../Redux/Reduser";
import Product from "./Product";


const ProductContainer = ({getOneXiaomiThunk,getOneAppleThunk,
                              addInBasketThunk,countBasketThunk,
                              getBasketThunk,
                              oneApple,oneXiaomi,
                              basket,match,CountBasketProducts,...props}) => {
    const [Render, setRender] = useState(true)
    useEffect(()=>{
        let id=match.params.id;
        if(id>=10) {
            getOneXiaomiThunk(id);
        }
    },[getOneXiaomiThunk,match.params.id])


    useEffect(()=>{
        let id = match.params.id;
        if(id<10) {
            getOneAppleThunk(id);
        }
    },[getOneAppleThunk,match.params.id])



    const addSmart = (name,photo,price) => {
            addInBasketThunk(name,photo,price);
            
    }

    const CountBasket=(id,count)=>{
        countBasketThunk(id,count);

    }
    useEffect(()=>{
        getBasketThunk();
    },[getBasketThunk])

    let Price = basket.map(p => p.price);
    let Count = basket.map(c => c.count);
    let total = 0;
    for (let i = 0; i < basket.length; i++) {
        total = total + Price[i] * Count[i]

    }


    let countBasket = 0;
    for (let i = 0; i < basket.length; i++) {
        countBasket = countBasket + Count[i]
    }
    CountBasketProducts(countBasket);


    useEffect(() => {
        if (Render === true) {
            getBasketThunk();
            setRender(false)
        }

    }, [getBasketThunk]);



    return<Product basket={basket}
                   CountBasket={CountBasket}
                   addSmart={addSmart}
                   {...props}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        oneApple: state.Reduser.oneApple,
        oneXiaomi:state.Reduser.oneXiaomi,
        description: state.Reduser.description,
        basket:state.Reduser.basket
    }
}

export default compose(
    connect(mapStateToProps,{getXiaomiThunk,getAppleThunk,getOneAppleThunk,
        getOneXiaomiThunk,getDescriptionThunk,addInBasketThunk,
        countBasketThunk,getBasketThunk,searchAC,CountBasketProducts}),
    withRouter
)(ProductContainer)
