import React, {useEffect} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import Basket from "./Basket";
import {deleteInBasketThunk, getBasketThunk} from "../../Redux/Reduser";


const BasketContainer = (props) => {

    useEffect(() => {
        props.getBasketThunk();
    }, [])

    const deleteSmart = (id) => {
        const delSmart = props.basket
        props.deleteInBasketThunk(props.id, delSmart);
    }


    return <Basket deleteSmart={deleteSmart} {...props}/>
}


const mapStateToProps = (state) => {
    return {
        basket: state.Reduser.basket
    }
}

export default compose(
    connect(mapStateToProps, {getBasketThunk, deleteInBasketThunk}),
    withRouter
)(BasketContainer)
