import React, {useEffect, useState} from "react";
import Main from "./Main";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {getAppleThunk, getXiaomiThunk,getBasketThunk} from "../../Redux/Reduser";


const MainContainer = ({getAppleThunk,getXiaomiThunk,xiaomi,apple,Search,CountBasketProducts,countBasketProducts,basket,getBasketThunk}) => {

    const [Render, setRender] = useState(true)
    useEffect(()=>{
        getAppleThunk();
    },[getAppleThunk])
    useEffect(()=>{
        getXiaomiThunk();
    },[getXiaomiThunk])

    let Price = basket.map(p => p.price);
    let Count = basket.map(c => c.count);
    let total = 0;
    for (let i = 0; i < basket.length; i++) {
        total = total + Price[i] * Count[i]

    }


    let countBasket = 0;
    for (let i = 0; i < basket.length; i++) {
        countBasket = countBasket + Count[i]
    }
    CountBasketProducts(countBasket);


    useEffect(() => {
        if (Render === true) {
            getBasketThunk();
            setRender(false)
        }

    }, [Price,getBasketThunk]);


    return<Main xiaomi={xiaomi} apple={apple}  Search={Search}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        Search:state.Reduser.search,
        basket:state.Reduser.basket,
        countBasketProducts:state.Reduser.countBasketProducts
    }
}

export default compose(
    connect(mapStateToProps,{getXiaomiThunk,getAppleThunk,getBasketThunk}),
    withRouter
)(MainContainer)
