import React from "react";
import styles from "./Product.module.css"
import basket from "../../Assets/img/basket.svg"
import {Link} from "react-router-dom";



const Product = ({CountBasket,addSmart,
                     xiaomi,apple,
                     oneApple,oneXiaomi,
                     description,basket,
                     name,photo,price,...props}) =>{


    const AddSmart = () => {
        let basketId = basket
            .filter(n => n.name === props.name)
            .map(n => n.id)

        if (basketId.length === 0) {

            addSmart(props.name, props.photo, props.price)
        } else {
            let basketCount = basket.filter(b => b.id === basketId[0]).map(b => b.count)

            CountBasket(basketId[0], basketCount[0] + 1);
        }
    }
    return<div className={styles.product}>
        <div>
            <Link to={'/smart/'+props.id}>
                <img className={styles.productPhoto} alt="photoProduct" src={props.photo}/>
            </Link>
        </div>
        <div>
            <Link to={'/smart/'+props.id}>{props.name}</Link>
        </div>
        <div>
            {props.price}$
        </div>
        <button onClick={AddSmart}><img className={styles.basket} src={basket}/>  add to Basket</button>
    </div>
}

export default Product;
