import React, {useEffect, useState} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {
    getAppleThunk,
    getOneAppleThunk,
    getXiaomiThunk,
    getOneXiaomiThunk,
    getDescriptionThunk,
    addInBasketThunk,
    countBasketThunk,
    getBasketThunk,
    searchAC
} from "../../Redux/Reduser";
import Product from "./Product";


const ProductContainer = ({getOneXiaomiThunk,getOneAppleThunk,
                              addInBasketThunk,countBasketThunk,
                              getBasketThunk,
                              oneApple,oneXiaomi,
                              description,basket,...props}) => {
    const[Render,setRender]=useState(false)

    useEffect(()=>{
        let id=props.match.params.id;
        if(id>=10) {
            getOneXiaomiThunk(id);
        }
    },[getOneXiaomiThunk])


    useEffect(()=>{
        let id = props.match.params.id;
        if(id<10) {
            getOneAppleThunk(id);
        }
    },[getOneAppleThunk])



    const addSmart = (name,photo,price) => {
            addInBasketThunk(name,photo,price);
        setRender(true)
    }

    const CountBasket=(id,count)=>{
        countBasketThunk(id,count);
        setRender(true)
    }
    useEffect(()=>{
        getBasketThunk();
        setRender(false)
    },[Render])



    return<Product basket={basket}
                   CountBasket={CountBasket}
                   addSmart={addSmart}
                   {...props}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        oneApple: state.Reduser.oneApple,
        oneXiaomi:state.Reduser.oneXiaomi,
        description: state.Reduser.description,
        basket:state.Reduser.basket
    }
}

export default compose(
    connect(mapStateToProps,{getXiaomiThunk,getAppleThunk,getOneAppleThunk,
        getOneXiaomiThunk,getDescriptionThunk,addInBasketThunk,
        countBasketThunk,getBasketThunk,searchAC}),
    withRouter
)(ProductContainer)
