import React, {useEffect, useState} from "react";
import FeedBack from "./FeedBack";
import {connect} from "react-redux";
import {addFeedBackThunk, getFeedBackThunk} from "../../Redux/Reduser";

export const FeedBackContainers=({getFeedBackThunk,feedback,addFeedBackThunk,oneApple,oneXiaomi})=>{

    const[render,setRender]=useState(true)
    const addFeedback=(name,id,feedBack,rating)=>{
        addFeedBackThunk(name,id,feedBack,rating)
        setRender(true)
    }
    useEffect(()=>{
        if(render===true){
        getFeedBackThunk();
        setRender(false)
        }
    },[render,getFeedBackThunk,addFeedBackThunk])


    return <FeedBack addFeedback={addFeedback} oneApple={oneApple} oneXiaomi={oneXiaomi} feedback={feedback}/>
}

const mapStateToProps=(state)=>{
    return{
        feedback:state.Reduser.feedback,
        oneApple:state.Reduser.oneApple,
        oneXiaomi:state.Reduser.oneXiaomi
    }
}

export default connect(mapStateToProps,{getFeedBackThunk,addFeedBackThunk})(FeedBackContainers)
