import React, {useEffect} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {getAppleThunk} from "../../Redux/Reduser";
import Apple from "./Apple";


const AppleContainer = ({getAppleThunk,apple,Search}) => {

    useEffect(()=>{
        getAppleThunk();
    },[getAppleThunk])
    return<Apple Search={Search} apple={apple}  />
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        Search:state.Reduser.search
    }
}

export default compose(
    connect(mapStateToProps,{getAppleThunk}),
    withRouter
)(AppleContainer)
