import React, {useEffect} from "react";

import {connect} from "react-redux";
import {
    CountBasketProducts,
    getAppleThunk, getBasketThunk,
    getXiaomiThunk
} from "./Redux/Reduser";
import App from "./App";


const AppContainer = ({getAppleThunk,getXiaomiThunk,getBasketThunk,CountBasketProducts,basket}) => {





    useEffect(()=>{
        getAppleThunk();
    },[getAppleThunk])
    useEffect(()=>{
        getXiaomiThunk();
    },[ getXiaomiThunk])
    useEffect(()=>{
        getBasketThunk();
    })

 return <App />
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        basket:state.Reduser.basket


    }
}

export default connect(mapStateToProps,{getAppleThunk,getXiaomiThunk,getBasketThunk,CountBasketProducts})(AppContainer)
