import React, {useEffect} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import Basket from "./Basket";
import {countBasketThunk, deleteInBasketThunk, getBasketThunk} from "../../Redux/Reduser";


const BasketContainer = (props) => {



    const deleteSmart = (id) => {
        const delSmart = props.basket
        props.deleteInBasketThunk(id, delSmart);
    }

    const CountBasket=(id,count)=>{
        if(count===0){
            deleteSmart(id)
        }
        else {
            props.countBasketThunk(id, count);
        }
    }

    useEffect(() => {
        props.getBasketThunk();
    }, [props])

    return <Basket CountBasket={CountBasket}  deleteSmart={deleteSmart} {...props}/>
}


const mapStateToProps = (state) => {
    return {
        basket: state.Reduser.basket
    }
}

export default compose(
    connect(mapStateToProps, {getBasketThunk, deleteInBasketThunk,countBasketThunk}),
    withRouter
)(BasketContainer)
