import React, {useEffect} from "react";
import Main from "./Main";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {getAppleThunk, getXiaomiThunk} from "../../Redux/Reduser";


const MainContainer = ({getAppleThunk,getXiaomiThunk,xiaomi,apple,Search}) => {


    useEffect(()=>{
        getAppleThunk();
    },[getAppleThunk])
    useEffect(()=>{
        getXiaomiThunk();
    },[getXiaomiThunk])



    return<Main xiaomi={xiaomi} apple={apple}  Search={Search}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        Search:state.Reduser.search
    }
}

export default compose(
    connect(mapStateToProps,{getXiaomiThunk,getAppleThunk}),
    withRouter
)(MainContainer)
