import React, { useState} from "react";
import styles from "./Header.module.css"
import {Link} from "react-router-dom";
import basket from"../../Assets/img/basket.svg"

const Header = (props) => {

const[searchName,setSearch]=useState(props.Search);

const inputSearch = (e)=>{
    setSearch(e.currentTarget.value)
    props.searchs(e.currentTarget.value)

}

const onSearch = ()=>{
props.searchs(searchName)
}


    return<div className={styles.header}>
        <Link to="/" >Header</Link>
        <input onChange={inputSearch} value={searchName}/>
        <button onClick={onSearch}>search</button>
        <Link className={styles.basket} to="/basket">
            <img   src={basket}/>
            {props.countBasketProducts}
        </Link>
    </div>
}

export default Header;
