import React from "react";
import styles from"./Window.module.css"
import basket from"../../Assets/img/basket.svg"


const WindowProduct =(props)=>{
    console.log(props)

const AddSmart=()=>{
if(props.oneApple.length!==0) {
    props.addSmart(props.oneApple.name, props.oneApple.photo, props.oneApple.price)
}
    props.addSmart(props.oneXiaomi.name,props.oneXiaomi.photo,props.oneXiaomi.price)

}

    let Description=props.description.filter(des=>props.oneApple.id===des.listId ||props.oneXiaomi.id===des.listId)
        .map(d=><Descriptions key={d.id}{...d}/>)


    return<div className={styles.window}>
    <div className={styles.image}>
        <img src={props.oneXiaomi.photo }/>
        <img src={props.oneApple.photo}/>
    </div>
        <div className={styles.name}>
            <div>
                {props.oneApple.name || props.oneXiaomi.name}
            </div>
            <div>
                {props.oneApple.memory || props.oneXiaomi.memory}
            </div>
            <div>
                {props.oneApple.price || props.oneXiaomi.price}
            </div>
            <button onClick={AddSmart} className={styles.buttonToBasket}>
              <img alt="basket" src={basket}/>  Add to Basket
            </button>
        </div>


        <div className={styles.Descriptions}>
            <span>specifications</span>
            {Description}
            <iframe width="680" height="390" src={props.oneApple.video || props.oneXiaomi.video} frameBorder="0"
                    allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen/>
        </div>

    </div>
}


export const Descriptions = (props) => {
    return <div>{Object.keys(props).map(key=><DescriptionTitle key={key} Title={key} Value={props[key]}/>)}</div>
}
export const DescriptionTitle = ({Title, Value}) => {
    if(Title==="id"||Title==="listId"){
        return null
    }
    return <div><b>{Title}</b> : {Value}</div>
}



export default WindowProduct;
