import React, {useEffect} from "react";
import {connect} from "react-redux";
import {compose} from "redux";
import {withRouter} from "react-router-dom";
import {getAppleThunk, getXiaomiThunk, searchAC} from "../../Redux/Reduser";
import SearchBox from "./SearchBox";


const SearchBoxContainer = ({getAppleThunk,getXiaomiThunk,xiaomi,apple,searchBox,searchAC}) => {


    useEffect(()=>{
        getAppleThunk();
    },[getAppleThunk])
    useEffect(()=>{
        getXiaomiThunk();
    },[getXiaomiThunk])



    return<SearchBox xiaomi={xiaomi} apple={apple} searchAC={searchAC} searchBox={searchBox}/>
}



const mapStateToProps = (state) => {
    return {
        apple: state.Reduser.apple,
        xiaomi: state.Reduser.xiaomi,
        searchBox:state.Reduser.searchBox
    }
}

export default compose(
    connect(mapStateToProps,{getXiaomiThunk,getAppleThunk,searchAC}),
    withRouter
)(SearchBoxContainer)
