import React, {useEffect, useState} from "react";
import styles from "./Basket.module.css"


const Basket = ({basket,CountBasketProducts,CountBasket,deleteSmart,total}) => {


    let BasketElements = basket.map(b => <BasketElement CountBasket={CountBasket}
                                                        deleteSmart={deleteSmart} key={b.id} {...b}/>)
    return <div className={styles.basket}>
        <span>Basket</span>
        <div>
            {BasketElements}
        </div>
        <div>
            <span>
                Total: {total}$

            </span>

        </div>
        <button>buy</button>
    </div>
}

export default Basket;

const BasketElement = (props) => {
    const [count, setCount] = useState(props.count)
    let CountMinus = () => {

        setCount(count - 1)
        if (count <= 0) {
            setCount(0)
        }
        props.CountBasket(props.id, count - 1)
    }
    let CountPlus = () => {
        setCount(count + 1)
        props.CountBasket(props.id, count + 1)
    }


    return <div className={styles.basketElement}>
        <img src={props.photo} alt="photoSmartphone"/>
        <span className={styles.basketCount}>
            {props.name}
            <button onClick={CountMinus}>-</button>
            <input className={styles.count} readOnly value={count} type="number"/>
            <button onClick={CountPlus}>+</button>
            {props.price * count}$
            <button onClick={() => props.deleteSmart(props.id)}>delete</button>
        </span>

    </div>

}
